import React from "react";

const categories = [
  {
    title: "FOODS",
    image: "/foods.jpg", // change to your actual image path
  },
  {
    title: "HERBS",
    image: "/herbs.jpg",
  },
  {
    title: "SPICES",
    image: "/spices.jpg",
  },
  {
    title: "TEA",
    image: "/tea.jpg",
  },
];

export default function Category() {
  return (
    <section className="w-full py-12 bg-white">
      <div className="max-w-7xl mx-auto px-6">
        {/* Heading */}
        <h2 className="text-3xl md:text-3xl font-bold text-green-800 text-left mt-10 mb-10">
          FEATURED CATEGORIES
        </h2>

        {/* Categories grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
          {categories.map((cat, index) => (
            <div
              key={index}
              className="relative group overflow-hidden rounded-lg shadow-md cursor-pointer"
            >
              <img
                src={cat.image}
                alt={cat.title}
                className="w-full h-80 object-cover transform group-hover:scale-105 transition duration-500"
              />
              {/* Overlay */}
              <div className="absolute inset-0 bg-black bg-opacity-40 flex flex-col items-center justify-center opacity-0 group-hover:opacity-100 transition duration-300">
                <p className="text-white text-lg font-bold">{cat.title}</p>
                <div className="mt-2 w-10 h-10 rounded-full bg-white flex items-center justify-center">
                  <span className="text-black text-xl">âž”</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
