import React from "react";
import {Link } from "react-router-dom";

const categories = [
  {
    title: "Cinnamon",
    image: "/11.jpg", // change to your actual image path
  },
  {
    title: "Paprika",
    image: "/14.jpg",
  },
  {
    title: "Black Pepper",
    image: "/13.jpg",
  },
  {
    title: "Turmeric",
    image: "/12.jpg",
  },
];

export default function Category() {
  return (
    <section className="w-full py-12 bg-white">
      <div className="max-w-7xl mx-auto px-6">
        {/* Heading */}
        <h1 className="font-serif text-yellow-800  mt-10 mb-3 text-left text-xl">Net Spice's</h1>
         
        <h1 className="text-2xl md:text-3xl font-bold text-green-800  mt-3 mb-10 text-left">
          FEATURED CATEGORIES
        </h1>

        {/* Categories grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
          {categories.map((cat, index) => (
            <div
              key={index}
              className="relative overflow-hidden shadow-md rounded-sm"
            >
              {/* Image */}
              <img
                src={cat.image}
                alt={cat.title}
                className="w-full h-96 object-cover"
              />

              {/* Name box overlay */}
              <div className="absolute bottom-0 left-0 w-full bg-white bg-opacity-80 flex justify-between items-center px-4 py-7">
                <p className="text-gray-600 font-semibold text-lg">{cat.title}</p>
                <button className="w-10 h-10 flex items-center justify-center rounded-full bg-gray-800 text-white hover:bg-green-700 transition">
                  ➔
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* See More button */}
        <div className="flex justify-center mt-10">
      <Link to='/'>
          <button className="px-6 py-2 border-2 border-green-800 text-green-800 font-semibold rounded-md hover:bg-green-800 hover:text-white transition">
            See More
          </button>
      </Link>
        </div>
      </div>
    </section>
  );
}
