import React, { useState, useRef, useEffect } from "react";
import { NavLink, Link } from "react-router-dom";

export default function Nav() {
  const [open, setOpen] = useState(false);
  const menuRef = useRef(null);

  const linkClass = ({ isActive }) =>
    `block py-2 px-3 rounded hover:text-blue-600 transition ${
      isActive ? "text-blue-600 font-semibold" : "text-white"
    }`;

  return (
    <header className="w-full bg-[#152310] shadow-md fixed top-0 z-50 md:h-36">
      <div className="max-w-7xl mx-auto flex items-center justify-between px-4 py-20 md:py-7">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-2">
          <img src="/logo.png" alt="Logo" className="h-16 w-auto" />
        </Link>

        {/* Desktop Nav */}
        <nav className="hidden md:flex space-x-6">
          <NavLink to="/" className={linkClass}>Home</NavLink>
          <NavLink to="/about" className={linkClass}>About</NavLink>
          <NavLink to="/services" className={linkClass}>Services</NavLink>
          <NavLink to="/contact" className={linkClass}>Contact</NavLink>
        </nav>

        {/* Hamburger (Mobile Only) */}
        <button
          className="md:hidden flex flex-col justify-between w-6 h-5 focus:outline-none"
          onClick={() => setOpen(!open)}
        >
          <span
            className={`h-0.5 w-6 bg-white transition-transform duration-300 ${
              open ? "rotate-45 translate-y-2" : ""
            }`}
          ></span>
          <span
            className={`h-0.5 w-6 bg-white transition-opacity duration-300 ${
              open ? "opacity-0" : ""
            }`}
          ></span>
          <span
            className={`h-0.5 w-6 bg-white transition-transform duration-300 ${
              open ? "-rotate-45 -translate-y-2" : ""
            }`}
          ></span>
        </button>
      </div>

      {/* Mobile Nav */}
      <div
        ref={menuRef}
        className={`md:hidden bg-[#152310] border-t overflow-hidden transition-all duration-300 ${
          open ? "max-h-96" : "max-h-0"
        }`}
      >
        <ul className="flex flex-col space-y-2 px-4 py-4">
          <li>
            <NavLink to="/" className={linkClass} onClick={() => setOpen(false)}>
              Home
            </NavLink>
          </li>
          <li>
            <NavLink to="/about" className={linkClass} onClick={() => setOpen(false)}>
              About
            </NavLink>
          </li>
          <li>
            <NavLink to="/services" className={linkClass} onClick={() => setOpen(false)}>
              Services
            </NavLink>
          </li>
          <li>
            <NavLink to="/contact" className={linkClass} onClick={() => setOpen(false)}>
              Contact
            </NavLink>
          </li>
        </ul>
      </div>
    </header>
  );
}
