import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import "../styles.css";

import { Autoplay, Pagination, Navigation } from "swiper/modules";

export default function ImageSlider() {
  return (
    <div className="relative w-full flex justify-center">
      <Swiper
        spaceBetween={20}
        centeredSlides={true}
        autoplay={{
          delay: 3000,
          disableOnInteraction: false,
        }}
        pagination={{
          clickable: true,
        }}
        navigation={true}
        modules={[Autoplay, Pagination, Navigation]}
        className="mySwiper w-full max-w-lg sm:max-w-xl md:max-w-2xl"
      >
        <SwiperSlide className="flex justify-center">
          <img
            src="/5.jpg"
            alt="1"
            className="w-[90%] sm:w-[85%] md:w-[80%] h-48 sm:h-64 md:h-[400px] object-cover rounded-lg shadow"
          />
        </SwiperSlide>

        <SwiperSlide className="flex justify-center">
          <img
            src="/6.jpg"
            alt="2"
            className="w-[90%] sm:w-[85%] md:w-[80%] h-48 sm:h-64 md:h-[400px] object-cover rounded-lg shadow"
          />
        </SwiperSlide>

        <SwiperSlide className="flex justify-center">
          <img
            src="/4.jpg"
            alt="3"
            className="w-[90%] sm:w-[85%] md:w-[80%] h-48 sm:h-64 md:h-[400px] object-cover rounded-lg shadow"
          />
        </SwiperSlide>
      </Swiper>

      {/* Custom Styling for Navigation & Pagination */}
      <style>
        {`
          /* Pagination dots */
          .swiper-pagination {
            bottom: -15px !important;
            text-align: center;
          }
          .swiper-pagination-bullet {
            width: 8px;
            height: 8px;
            background: #aaa;
            opacity: 0.7;
          }
          .swiper-pagination-bullet-active {
            background: #a65b05; /* spice brand color */
            opacity: 1;
          }

          /* Navigation arrows */
          .swiper-button-next,
          .swiper-button-prev {
            color: #a65b05;
            top: 50%;
            transform: translateY(-50%);
            padding: 8px;
            background: rgba(255, 255, 255, 0.7);
            border-radius: 9999px;
            box-shadow: 0 2px 6px rgba(0,0,0,0.2);
          }

          /* Smaller arrows on mobile */
          @media (max-width: 640px) {
            .swiper-button-next,
            .swiper-button-prev {
              width: 28px;
              height: 28px;
              font-size: 12px;
            }
          }
        `}
      </style>
    </div>
  );
}
