import React, { useEffect, useRef, useState } from "react";
import aboutImage from "/11.jpg"; // Replace with actual image path

export default function About() {
  const leftRef = useRef(null);
  const rightRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observerOptions = { threshold: 0.2 };

    const observerCallback = (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      });
    };

    const observer = new IntersectionObserver(observerCallback, observerOptions);

    if (leftRef.current) observer.observe(leftRef.current);
    if (rightRef.current) observer.observe(rightRef.current);

    return () => {
      if (leftRef.current) observer.unobserve(leftRef.current);
      if (rightRef.current) observer.unobserve(rightRef.current);
    };
  }, []);

  return (
    <div>
      {/* Hero / Top Section */}
      <section
        className="w-full h-[400px] sm:h-[500px] md:h-[600px] bg-center bg-cover bg-no-repeat bg-fixed flex flex-col justify-center items-center"
        style={{ backgroundImage: "url('/cinbg.jpg')" }} // replace with your image
      >
        <h1 className="text-white text-3xl sm:text-4xl md:text-5xl font-bold">
          ABOUT US
        </h1>
        <p className="text-white mt-2 sm:mt-4 text-sm sm:text-base md:text-lg">
          The journey of a spice exporter
        </p>
      </section>

      {/* Animated Scroll Content Section */}
      <section className="w-full bg-white py-16 px-6">
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center justify-between gap-12">
          {/* Text Block */}
          <div
            ref={leftRef}
            className={`w-full md:w-1/2 transition-opacity duration-700 ease-in-out ${
              isVisible ? "animate-slide-in-left" : "opacity-0"
            }`}
          >
            <h2 className="text-yellow-800 font-semibold text-lg mb-2">
              Introduction
            </h2>
            <h1 className="text-3xl sm:text-4xl font-bold mb-4">WHO WE ARE</h1>
            <p className="text-gray-700 text-justify leading-relaxed mb-4 text-sm sm:text-base">
              Abeywardena Sons Spice (Pvt) Ltd. is a leading exporter of premium
              True Ceylon Cinnamon, high-quality spices, and essential oils from
              Sri Lanka. With a well-established network of processing facilities
              and raw material collection centers across the island, we ensure the
              finest selection of authentic Sri Lankan spices, known for their
              rich aroma, superior flavor, and exceptional purity.
            </p>
            <p className="text-gray-700 text-justify leading-relaxed mb-4 text-sm sm:text-base">
              Our diverse range includes Ceylon cinnamon, black pepper, cloves,
              nutmeg, mace, turmeric, lemongrass, and more â€” processed with care
              to preserve their natural goodness. We also specialize in essential
              oils such as cinnamon bark oil, citronella oil, and lemongrass oil,
              extracted using advanced techniques to retain potency.
            </p>
            <p className="text-gray-700 text-justify leading-relaxed text-sm sm:text-base">
              At Abeywardena Sons, we take pride in our commitment to quality,
              sustainability, and customer satisfaction. Every product is rigorously
              tested to meet international standards, ensuring that our clients
              receive only the best.
            </p>
          </div>

          {/* Image Block */}
          <div
            ref={rightRef}
            className={`w-full md:w-1/2 transition-opacity duration-700 ease-in-out ${
              isVisible ? "animate-slide-in-right" : "opacity-0"
            }`}
          >
            <img
              src={aboutImage}
              alt="Spices"
              className="w-full h-auto rounded-lg shadow-lg"
            />
          </div>
        </div>
      </section>
    </div>
  );
}
