import React, { useState } from "react";
import { NavLink, Link } from "react-router-dom";


export default function Nav() {
  const [open, setOpen] = useState(false);

  const linkClass = ({ isActive }) =>
    `block py-2 px-3 rounded hover:text-blue-600 transition ${
      isActive ? "text-blue-600 font-semibold" : "text-gray-700"
    }`;

  return (
    <header className="w-full bg-black shadow-md fixed top-0 left-0 h-36 ">
      <div className="max-w-7xl mx-auto flex items-center justify-between px-4 py-3 border border-y-white">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-2">
          <img src='/logo.png' alt="Logo" className="h-10 w-auto" />
          <span className="font-bold text-lg text-gray-800">MyApp</span>
        </Link>

        {/* Desktop Nav */}
        <nav className="hidden md:flex space-x-6">
          <NavLink to="/" className={linkClass}>Home</NavLink>
          <NavLink to="/about" className={linkClass}>About</NavLink>
          <NavLink to="/services" className={linkClass}>Services</NavLink>
          <NavLink to="/contact" className={linkClass}>Contact</NavLink>
        </nav>

        {/* CTA + Hamburger */}
        <div className="flex items-center space-x-3">
         

          {/* Hamburger button */}
          <button
            className="md:hidden flex flex-col justify-between w-6 h-5 focus:outline-none"
            onClick={() => setOpen(!open)}
          >
            <span
              className={`h-0.5 w-6 bg-gray-800 transition-transform duration-300 ${
                open ? "rotate-45 translate-y-2" : ""
              }`}
            ></span>
            <span
              className={`h-0.5 w-6 bg-gray-800 transition-opacity duration-300 ${
                open ? "opacity-0" : ""
              }`}
            ></span>
            <span
              className={`h-0.5 w-6 bg-gray-800 transition-transform duration-300 ${
                open ? "-rotate-45 -translate-y-2" : ""
              }`}
            ></span>
          </button>
        </div>
      </div>

      {/* Mobile Nav */}
      {open && (
        <nav className="md:hidden bg-white border-t shadow-sm">
          <ul className="flex flex-col space-y-2 px-4 py-4">
            <li><NavLink to="/" className={linkClass} onClick={() => setOpen(false)}>Home</NavLink></li>
            <li><NavLink to="/about" className={linkClass} onClick={() => setOpen(false)}>About</NavLink></li>
            <li><NavLink to="/services" className={linkClass} onClick={() => setOpen(false)}>Services</NavLink></li>
            <li><NavLink to="/contact" className={linkClass} onClick={() => setOpen(false)}>Contact</NavLink></li>
          </ul>
        </nav>
      )}
    
    </header>
  );
}
