import React from "react";
import {Link } from "react-router-dom";

export default function Hero() {
  return (
         <section className="w-full h-[500px] bg-gray-200  flex items-center justify-center">
      <div className="flex justify-between items-center max-w-7xl mx-auto space-x-3 gap-60">
        <div className="w-1/2">
        {/* Heading */}
        <h1 className="font-serif text-yellow-800 mt-10 mb-3 text-left text-xl">
          Net Spice's
        </h1>
        <h1 className="text-2xl md:text-3xl font-bold text-green-800  text-left">
         ALL SPICES AT ONE PLACE
        </h1>
       <br />
       <br />
       <p className="text-justify">Sri Lanka, renowned as the island of spices, is home to Zest Ceylon—your ultimate online destination for premium Sri Lankan spices. Whether you’re seeking bold, aromatic flavors or stocking up in bulk, Zest Ceylon brings you a rich selection of both organic and conventional spices, all in one place. As one of the country’s leading wholesale spice suppliers, we’re proud to offer top-quality herbs, spices, and culinary ingredients at the best prices. Taste the essence of Sri Lanka—pure, fresh, and full of zest.</p>
      </div>
      <div className="w-1/2">
      <img src="/all.jpg" alt=" spice"  className="h-[400px] rounded-xl items-center"/>
      </div>
      </div>
    </section>
  );
}
